   changed: function (id, s) {
		var ip = Sensori.findOne({_id: id}).ip;
			if (Sensori.findOne({_id: id}).on === true) {
			// shutdown
			if (SyncedCron.nextScheduledAtDate(ip +'_read sensors')) { SyncedCron.remove(ip +'_read sensors');}
