function cron_watt_after (now, response) {
	var today = moment().format("YYYYMMDD");	
	var matrix =[]; var csvfs ='';var solararr=[];
    if (Raspberry) {csvfs = csvpath+today+".csv";} else {csvfs = csvpathwin;}

	// Leggo immediatamente i valori de sensore per garantire il timeframe dei 5 minuti 		
	if (fs.existsSync(csvfs)) {      
        matrix = (fs.readFileSync(csvfs).toString().split("\n").map(function(row){ return { x: moment(today +" "+ row.split(",")[0],"YYYYMMDD HH:mm").unix(), y: Number(row.split(",")[27])};})).slice(1,-1);
		solararr = solar(matrix);
		//console.log('solarray',solarray);
        Watt.update({day: today}, { $set: {solararr: solararr}},{upsert: true, multi: false});
	} else  {console.log('File not found: ',csvfs);} 

	if (response.data !== null) {       
		var pulsearr = pulse (today,now,response.data.Sensors[0].Total,solararr);      
	//	console.log('pulserray',pulsearr);      
        Watt.update({day: today}, { $set: {pulsearr: pulsearr}},{upsert: true, multi: false});
    } else { 
		// console.log('Warning cron_watt (problem reading sensor):',today,' - ',response); 
			}
}


function pulse (date,now,ytmp,solararr) {
	var b = []; var x0 = 0; var y0 = 0; var e = Watt.findOne({day: date}); var sx=0; var sy=0;
	console.log('solararr.length',solararr.length);

	if (solararr.length > 0) {sx = solararr[solararr.length-1].x; sy = solararr[solararr.length-1].y; }
	if (e && e.pulsearr)  {
		e.pulsearr.push({x: now, y: 0, ytmp: ytmp });
		e.pulsearr.forEach(function(p) {
			var x1 = p.x; var y1 = p.ytmp; var deltat = (x1-x0)/1000;
			if (deltat <=0 || y1 < y0)  {console.log('!!!  y1:',y1,'y0:',y0,'x1:',x1,'x0:',x0);};

			if (deltat > 0 && (y1 - y0) >= 0 ) {              
				var y = Math.round(((y1 - y0)*3600/deltat),3);            
                if (now === sx) { if (sy >= y) {y = (sy + y);} else {y = (sy - y);} }
                console.log('now',now,'sx',sx,'sy',sy,'y',y,'y1',y1,'y0',y0);
				b.push({x: x1, y: y, ytmp: y1 });
			} else {console.log('Warning (pulse) deltat or (y1-y0):', deltat, '|', y1-y0);}           
			x0 = x1; y0 = y1;
        });
	} else {b.push({x: now, y: 0, ytmp: ytmp }); console.log('First entry for pulsearr:', now);}  
	return b; 
}


function solar(matrix) {
    var b = []; var x0 = 0; var y0 = 0;
	if (matrix)  {
		matrix.forEach(function(s) {
			var x1 = s.x; var y1 = s.y; var deltat = x1-x0;
			if (deltat > 0) {
				var KwFull = Math.round(((y1 - y0)*3600/deltat*1000),3);
				//console.log('solar:',{x: x1*1000, y: KwFull });
				b.push({x: x1*1000, y: KwFull });					
			} else {
            //  console.log('Warning (solar) deltat:', deltat);
            }
			x0 = x1; y0 = y1;
		});
	} else {console.log('No found MATRIX');}
	return b;
}


function cron_watt () {
	var now = Math.floor((moment().unix()));
	var ip = Sensori.findOne({tipo: 'pulse'}).ip;
	// Leggo immediatamente i valori del sensore per garantire il timeframe dei 5 minuti  
	var response = HTTP.get("http://"+ip+"/json", {});
	setTimeout(cron_watt_after(now*1000, response), 10000);	
}


